const createMenu = () => {
    const el = document.createElement(DROPDOWN_MENU_TAG_NAME);
    el.innerHTML = `
<span slot="menu-button">Menu Label</span>
<${DROPDOWN_MENUITEM_TAG_NAME}><span>action 1</span></${DROPDOWN_MENUITEM_TAG_NAME}>
